import React from "react";

const Card = ({ pokemon, loading, infoPokemon }) => {
    return (
        <>
            {loading ? (
                <h1>Loading...</h1>
            ) : (
                pokemon.map((item) => (
                    <a href="#" key={item.id} onClick={(e) => { e.preventDefault(); infoPokemon(item) }} target="_blank">
                        <div className="card">
                            <h2>{item.id}</h2>
                            <img src={item.sprites.front_default} alt="" />
                            <h2>{item.name}</h2>
                        </div>
                    </a>
                ))
            )}
        </>
    );
};

export default Card;
